                        color: "steelblue"
                    },
                    {
                        titulo: "IPC",
                        data: result.ipc,
                        color: "#ff7f0e"
                    }
                ];
                chart.datos = datos;
                chart.periodos = periodos;
                chart.m_graficar();
            }
            ,
            error: function (jqXHR, textStatus, errorThrow) {
                console.error(jqXHR['responseText']);
            }
        });
    }

